// Функция для создания фигуры из массивов
const createMeshFromArrays = (vertices, faces, scene) => {
    const positions = [];
    const indices = [];
    const uvs = [];

    vertices.forEach(vertex => {
        positions.push(vertex[0], vertex[1], vertex[2]);
    });

    faces.forEach(face => {
        indices.push(face[0], face[1], face[2]);
        indices.push(face[2], face[1], face[0]);
    });

    const uv1 = [0, 0, 1, 0, 1, 1, 0, 1];
    for (let i = 0; i < faces.length; i++) {
        uvs.push(...uv1);
    }

    const mesh = new BABYLON.Mesh("customMesh", scene);
    const vertexData = new BABYLON.VertexData();
    vertexData.positions = positions;
    vertexData.indices = indices;
    vertexData.uvs = uvs;

    vertexData.applyToMesh(mesh, true);

    const material = new BABYLON.StandardMaterial("material", scene);
    material.diffuseTexture = new BABYLON.Texture("https://i.postimg.cc/y83ChCs2/image.png", scene); // Текстура для фигуры
    material.backFaceCulling = false;

    mesh.material = material;

    return mesh;
};

